import React, { PureComponent } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import "./index.scss";
import Home from "../pages/home";
import About from "../pages/about";

class AppRouter extends PureComponent {
  state = {
    routeConfig: [
      { title: "首页", path: "/", exact: true, component: Home },
      { title: "关于", path: "/about", exact: true, component: About },
    ],
  };

  changeTab(tab) {
    // this.setState({
    //   selectedTab: tab
    // })
    console.log(tab);
  };
  render() {
    return (
      <Router>
        <div className="layout">
          <div className="main-box">
            <div className="main-content">
              {/* exact精准匹配 */}
              {this.state.routeConfig.map((item, index) => {
                return <Route key={index} path={item.path} exact={item.exact} component={item.component} />;
              })}
            </div>
          </div>
          <div className="tabBar">
            <ul>
              {this.state.routeConfig.map((item, index) => {
                return (
                  <li key={index} >
                    <Link onClick={this.changeTab.bind(this,index)} to={item.path}>{item.title}</Link>
                  </li>
                );
              })}
            </ul>
          </div>
        </div>
      </Router>
    );
  }
}

export default AppRouter;
